DASHBOARD_TS_REPLACE_KEYS = [
    '''import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { AccountmanagerService } from '../services/accountmanager.service';''',
    '''  sidebar = [];
  constructor(private router:Router, private am: AccountmanagerService) {
    let all_config = this.router.config;
    all_config.forEach(element => {
      if (element.canActivate) {
        this.sidebar.push({
          name: element.path.toString().replace(/-/g, " "),
          path: element.path.toString()
        })
        
      }
    });

    console.log(this.sidebar);
  }

  ngOnInit(): void {

  }

  logout() {
    this.am.logout();
  }'''
]

def replace_for_dashboard(filename):
    data_file = open(filename, "r").read()
    for f, r in zip(DASHBOARD_TS_SEARCH_KEYS, DASHBOARD_TS_REPLACE_KEYS):
        data_file = data_file.replace(f, r)
    return data_file