SIGNUP_COMPONENT_TS = '''import { Component, OnInit } from '@angular/core';
import { ApiserviceService } from '../services/apiservice.service';
import { AccountmanagerService } from '../services/accountmanager.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit {
  email = "";
  password = "";
  error = "";
  name = "";
  constructor(private api: ApiserviceService, private am: AccountmanagerService, private router:Router) { 
    if (this.am.is_logged_in()) {
      this.router.navigate(["/REDIRECT_TO"]);
    }
   }

  ngOnInit() {
  }

  signup() {
    if (this.email.length < 10) {
      this.error = "Invalid Email Address";
    } else if (this.password.length < 6) {
      this.error = "Password should be of minimum 6 characters";
    } else if (this.name.length < 6) {
      this.error = "Name should be of minimum 6 characters";
    } else {
      this.api.signup(
        {
          "email": this.email,
          "password": this.password,
          "name": this.name,
        }
      ).subscribe(
        (data) => {
          this.am.save_user_data(data["payload"], true);
        },
        (error) => {
          if (error.error.message) {
            this.error = error.error.message;
          } else {            
            this.error = "Server Error";
          }
        }
      )
    }
  }
}
'''

SIGNUP_COMPONENT_HTML = '''
<div class="center bg-signup">
  <div class="card">
     <div class="logo">
         <img src="https://ask-jennie-assets.s3.ap-south-1.amazonaws.com/lock.png" width="80px" height="80px">
     </div>
     <div class="title-box">
     <h2 class="title">Register</h2>
     <p class="subheading-title" *ngIf="this.error == ''">Already have an account? <a href="login">Login</a></p>
     <div class="error-msg" *ngIf="this.error != ''">{{error}}</div>
     </div>

    <div class="form-group">
        <input type="email" class="form-control" [(ngModel)]="name" [ngModelOptions]="{standalone: true}" placeholder="Full Name">
    </div>       
    <div class="form-group">
        <input type="email" class="form-control" [(ngModel)]="email" [ngModelOptions]="{standalone: true}" placeholder="Email">
    </div>   
    <div class="form-group">
      <input type="password" class="form-control" [(ngModel)]="password" [ngModelOptions]="{standalone: true}" placeholder="Password">
    </div>  
    <div class="form-group">
      <button class="btn btn-primary btn-signup" (click)="signup()">Registration</button>
    </div> 
</div>'''


