def update_url(endpoint, view):
    urls_file = open("urls.py").read()
    file_modified = ""
    for line in urls_file.split("\n"):
        try:
            if "]" in line:
                file_modified += "    path('" + endpoint + "', " + view + ".as_view())," + "\n" + line + "\n"
            else:
                file_modified += line + "\n"
        except Exception as e:
            pass

    open("urls.py", "w").write(file_modified)
