import json

class JSONHelper():
    def __init__(self):
        """
        JSON Helper class is to provide some basic json function.
        In future the class may have convertion and writing of json
        to any kind of tabulated file. eg *.db, *.csv, *.xlsx, *.pdf etc.

        # init class
        jsonHelper = JSONHelper()
        """
    def read_json(self, jsonFilePath):
        """
        Read JSON File from file path

        # Read a json file.
        jsonHelper.read_json("path/to/json/file")
        :param `jsonFilePath: JSON File Path`
        :return: `json data from file.`
        """
        with open(jsonFilePath) as f:
            data = json.load(f)
        return data

    def write_json(self, jsonFilePath, jsonObject):
        """
        Write JSON to JSON File.

        # Write a json file.
        jsonHelper.read_json("path/to/json/file", json_object_to_write)
        :param jsonFilePath: JSON File Path.
        :param jsonObject: JSON object to save.
        :return: True
        """
        with open(jsonFilePath, 'w') as json_file:
            json.dump(jsonObject, json_file, indent=3)
        return True

    def validate_json(self, keys, jsonObject):
        for key in keys:
            if key not in jsonObject:
                return False, key

        return True, None